CREATE TABLE category (
  id bigint(20) NOT NULL AUTO_INCREMENT,
  description varchar(45) NOT NULL,
  PRIMARY KEY (id),
  UNIQUE KEY description_UNIQUE (description)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE tourist_spot (
  id bigint(20) NOT NULL AUTO_INCREMENT,
  name varchar(100) NOT NULL,
  longitude decimal(11,8) NOT NULL,
  latitude decimal(10,8) NOT NULL,
  category bigint(20) DEFAULT NULL,
  PRIMARY KEY (id),
  UNIQUE KEY name_UNIQUE (name),
  KEY fk_category_idx (category),
  CONSTRAINT fk_category FOREIGN KEY (category) REFERENCES category (id) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

 
CREATE TABLE user (
  id bigint(20) NOT NULL AUTO_INCREMENT,
  name varchar(100) NOT NULL,
  email varchar(45) NOT NULL,
  password varchar(500) NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE favorite_tourist_spot (
  user_id bigint(20) NOT NULL,
  tourist_spot_id bigint(20) NOT NULL,
  PRIMARY KEY (user_id,tourist_spot_id),
  KEY fk_touristic_spot_idx (tourist_spot_id),
  CONSTRAINT fk_touristic_spot_idx FOREIGN KEY (tourist_spot_id) REFERENCES tourist_spot (id) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT fk_user_idx FOREIGN KEY (user_id) REFERENCES user (id) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE snowman.picture_tourist_spot (
  id bigint(20) NOT NULL AUTO_INCREMENT,
  picture blob NOT NULL,
  tourist_spot bigint(20) NOT NULL,
  created_by bigint(20) NOT NULL,
  PRIMARY KEY (id),
  KEY fk_touristspot_picture (tourist_spot),
  KEY fk_user_picture (created_by),
  CONSTRAINT fk_touristspot_picture FOREIGN KEY (tourist_spot) REFERENCES tourist_spot (id) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT fk_user_picture FOREIGN KEY (created_by) REFERENCES user (id) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

